mapper.select:
SELECT article_id, subject, writer, regdate, hitcount, level_no FROM ( SELECT CEIL(rownum / #{elementSize}) request_page, article_id, subject, writer, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI') regdate, hitcount, level_no FROM ( SELECT article_id, subject, writer, regdate, hitcount, level_no FROM article
OR writer LIKE '%' || #{keyword} || '%'
OR subject LIKE '%' || #{keyword} || '%'
OR content LIKE '%' || #{keyword} || '%'
AND board_id = #{boardId}
ORDER BY group_no DESC, order_no ASC)) WHERE request_page = #{requestPage}
